import { Link } from 'react-router-bootstrap';

function Book(props) {
  const { book } = props;
  return (
    <div className="book" key={book.id}>
      <Link to={`/book/${book.id}`}>
        <img src={book.image} alt={book.name} />
      </Link>
      <div className="book-info">
        <a href={`/book/${book.id}`}>
          <p>{book.name}</p>
        </a>
        <p>
          <strong>${book.price}</strong>
        </p>
      </div>
      <button>Add to cart</button>
    </div>
  );
}
export default Book;
